export declare const addUserDbQuery = "\nWITH ins_user_core AS (\n    INSERT INTO user_core (email, PASSWORD, password_salt, country_code, mobile_number, user_handle)\n            VALUES ($1, $2, $3, $4, $5, $11)\n        RETURNING\n            id AS user_id),\n            ins_otp_user AS (\n                INSERT INTO otp_log (user_id, source, type, otp)\n                        VALUES ((\n                                SELECT\n                                    user_id\n                                FROM\n                                    ins_user_core),\n                                $8,\n                                $9,\n                                $10))\n        INSERT INTO user_profile (user_id, first_name, last_name)\n            VALUES ((\n                    SELECT\n                        user_id\n                    FROM\n                        ins_user_core),\n                    $6,\n                    $7)\n        RETURNING (\n            SELECT\n                user_id\n            FROM\n                ins_user_core);\n";
export declare const addUserSocialLoginDbQuery = "\nWITH ins_user_core AS (\n    INSERT INTO user_core (email, user_handle, is_social_login, is_active, is_verified)\n            VALUES ($1, $2, TRUE, TRUE, TRUE)\n        RETURNING\n            id AS user_id)\n        INSERT INTO user_profile (user_id, first_name, last_name)\n            VALUES ((\n                    SELECT\n                        user_id\n                    FROM\n                        ins_user_core),\n                    $3,\n                    $4)\n        RETURNING (\n            SELECT\n                user_id\n            FROM\n                ins_user_core);\n";
